<?php

namespace Kunstmaan\KMediaBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * GalleryRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SlideGalleryRepository extends EntityRepository
{

    public function getAllGalleries($limit = null)
    {
            $qb = $this->createQueryBuilder('slidegallery')
                       ->select('slidegallery')
                       ->where('slidegallery.parent is null');

            if (false === is_null($limit))
                $qb->setMaxResults($limit);

            return $qb->getQuery()
                      ->getResult();
    }
}